package RahulLogicaProgram;

public class IfEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int num1=48;
     if(num1%2==0) {
    	 System.out.println("Number is Even");
     }else {
    	 System.out.println("Number is Odd");
     }
    		
	}

}
