package RahulLogicaProgram;

public class Unaryop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int a=10;
  int b=2;
   System.out.println("Sysa is"+a);
   System.out.println("Sysa is"+b);
   
   
	int k=++a;
	System.out.println("Sysa is"+k);
	System.out.println("Sysa is"+a);
	
	int j=k++;
	System.out.println("Sysa is"+k);
	System.out.println("Sysa is"+j);
  }
	
	
}